package _01_conditional;

public class T01_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 100;
		
		if(num1 == 100)
			System.out.println("100점입니다.");
		
		if(num1 > 80)
			System.out.println("축하합니다.");
		
		System.out.println("합격입니다.");
		
		System.out.println("if 문과 상관없이 실행");
	}

}
